import React, { useContext, useState } from 'react';
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  Alert,
} from 'react-native';
import { AddUserToGame, doesDocExist } from '../firebase/Firebase';
import { AuthContext } from '../context/AuthContext';
import { useNavigation } from '@react-navigation/native';
import { useEffect } from 'react/cjs/react.development';

export default function JoinGame() {
  const navigation = useNavigation();
  const [key, setKey] = useState('');
  const { user } = useContext(AuthContext);
  const [ifDocExsists, setIfDocExsists] = useState();

  const test = () => {
    let num = 0;
    if (num != 0) {
      if (ifDocExsists === true) {
        console.log('sanning');
        AddUserToGame(user.displayName, user.email, key, setIfDocExsists);
      } else {
        console.log('inte sanning');
      }
      num += 1;
    }
  };
  useEffect(() => {
    let num = 0;
    if (num !== 0) {
      if (ifDocExsists === true) {
        console.log('sanning');
        AddUserToGame(user.displayName, user.email, key, setIfDocExsists);
      } else {
        console.log('inte sanning');
      }
      num + 1;
    }
  }, [ifDocExsists, key, user.displayName, user.email]);

  return (
    <View style={styles.container}>
      <View style={styles.textView}>
        <Text
          style={styles.text}
          onPress={() => console.log('emil', ifDocExsists)}
        >
          Enter game key
        </Text>
        <TextInput
          style={styles.textInput}
          placeholder="Enter key here:"
          onChangeText={(text) => setKey(text)}
        />
        <View style={styles.underLineView} />
      </View>
      <TouchableOpacity
        style={styles.button}
        onPress={() => {
          //AddUserToGame(user.displayName, user.email, key, setIfDocExsists);
          doesDocExist(key, setIfDocExsists);
          /*   if (AddUserToGame(user.displayName, user.email, key) === true) {
            navigation.navigate('ParticipantScreen', { gameKey: key });
          } else {
            Alert.alert('wrong key');
          } */
        }}
      >
        <Text style={styles.buttonText}>Join game</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'space-between',
    backgroundColor: '#146B66',
  },
  button: {
    backgroundColor: '#AFEFDF',
    width: '80%',
    height: '15%',
    marginBottom: 80,
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 20,
  },

  buttonText: {
    fontSize: 30,
    fontWeight: 'bold',
  },
  text: {
    fontSize: 22,
  },
  textInput: {
    marginTop: 40,
    fontSize: 20,
  },
  textView: {
    marginTop: 100,
    width: '80%',
    justifyContent: 'center',
    alignItems: 'center',
  },
  underLineView: {
    height: 2,
    backgroundColor: 'black',
    marginTop: 3,
    width: '100%',
  },
});
